import React, {useState} from 'react'
import { StyleSheet, Text, View, SafeAreaView, Button ,TextInput} from 'react-native';
import {Picker} from '@react-native-picker/picker';

export const AddDrinks = (props) => { 
    const [restaurantName, setRestaurantName] = useState("")
    const [bobaType, setBoba] = useState("")
    const [sugarLevel, setSugar] = useState()
    const [size, setSize] = useState()
    return(
        <SafeAreaView style={styles.container}>
            <Text style={styles.bodyText}>  Restaurant Name</Text>
            <TextInput  style={styles.bodyText} placeholder = "Kungfu Tea" 
            onChangeText = {restaurantName => setRestaurantName(restaurantName)}
            defaultValue= {restaurantName}/>
            <Text style={styles.bodyText}>  Boba Type</Text>
            <TextInput  style={styles.bodyText} placeholder = "Classic Milk Tea" 
            onChangeText = {bobaType => setBoba(bobaType)}
            defaultValue= {bobaType}/>
            <Text style={styles.bodyText}>  Sugar Level </Text>
            <Picker
                style = {styles.picker}
                selectedValue={sugarLevel}
                onValueChange={(itemValue, itemIndex) =>
                    setSugar(itemValue)
                }>
                <Picker.Item label="0%" value="0" />
                <Picker.Item label="25%" value="25" />
                <Picker.Item label="50%" value="50" />
                <Picker.Item label="100%" value="100" />
            </Picker>
            <Text style={styles.bodyText}>  Size </Text>
            <Picker
                style = {styles.picker}
                selectedValue={size}
                onValueChange={(itemValue, itemIndex) =>
                    setSize(itemValue)
                }>
                <Picker.Item label="S" value="S" />
                <Picker.Item label="M" value="M" />
                <Picker.Item label="L" value="L" />
            </Picker>
        </SafeAreaView>
    )
}

const styles = StyleSheet.create({
    headerText: {
        fontSize: 30,
        textAlign: "center",
        paddingTop: 20
    },
    bodyText: {
        fontSize: 20, 
        textAlign: "center",
        padding: 20
    },
    container: {
      display: "flex",
      backgroundColor: "white",
      justifyContent: "space-evenly",
      alignItems: "center",
    },
    picker: {
        width: "100%",
        height: 30,
        paddingTop: 0

    }
  });
